mapper.select:
select profile
from member
where no = #{no}

select no,
	   email,
	   provider,
	   name,
	   if(
	   		INSTR(profile,'google')>0,
	   		profile,
	   		if(
	   			INSTR(profile,'kakao')>0,
	   			profile,
	   			if(
	   				INSTR(profile,no)>0,
	   				concat('http://localhost:9000/profile/users/',profile),
	   				concat('http://localhost:9000/profile/default/',profile)
	   			)
	   		)
	   	) as profile
from member
where no = #{no}
